import React, { FunctionComponent } from "react";
import { BadgeSizes } from "../constants/BadgeConstants";
import { noop } from "../utils/misc";
import { BadgeIcon } from "./BadgeIcon";
import { VERIFIED_BADGE_SVG_URL_ENCODED_DATA_IMAGE } from "../styles/sharedVariables";

export interface VerifiedBadgeIconProps {
  size: BadgeSizes;
  titleText: string;
  additionalContainerClass?: string;
  overrideContainerClass?: string;
  additionalImgClass?: string;
  overrideImgClass?: string;
  onIconClick?: () => void;
}

export const verifiedBadgeIconDataAttrSelector =
  "data-rblx-verified-badge-icon";
export const verifiedBadgeIconReactRenderClass = "verified-badge-icon";
const verifiedBadgeDataAttrs = {
  [verifiedBadgeIconDataAttrSelector]: "",
};

export const VerifiedBadgeIcon: FunctionComponent<VerifiedBadgeIconProps> = ({
  size,
  titleText,
  additionalContainerClass,
  overrideContainerClass,
  additionalImgClass,
  overrideImgClass,
  onIconClick = noop,
}) => {
  if (!size) {
    throw new Error("Must provide a size prop");
  }

  if (!titleText) {
    throw new Error("Must provide a titleText for accessibility");
  }

  return (
    <BadgeIcon
      dataAttrs={verifiedBadgeDataAttrs}
      size={size}
      titleText={titleText}
      additionalContainerClass={additionalContainerClass}
      overrideContainerClass={overrideContainerClass}
      additionalImgClass={additionalImgClass}
      overrideImgClass={overrideImgClass}
      src={VERIFIED_BADGE_SVG_URL_ENCODED_DATA_IMAGE}
      onIconClick={onIconClick}
    />
  );
};

export default VerifiedBadgeIcon;
