// @ts-nocheck
export function noop() {}

const A11yButtonTriggerKeys = ["Enter", " "];

export const bindKeyDownA11yButton = (fnToCall = noop) => {
  return (event) => {
    if (A11yButtonTriggerKeys.includes(event.key)) {
      fnToCall();
    }
  };
};

export default {
  bindKeyDownA11yButton,
  noop,
};
